import React, { useState, useEffect } from "react";
import "./styles.css";

const Show = ({ value, data }) => {

    //console.log(value);

    const [isValid, setIsValid] = useState(false);

    useEffect(() => {
        setIsValid(data.func(value));
    }, [value,data]);

    return (
        <p className={isValid ? "valid" : "invalid"}>
            {data.title}
        </p>
    );
};

export default Show;