import React, { useEffect } from "react";

import Show from "./Show";

const Validate = ({ value, requirement, currStatus }) => {

    useEffect(() => {
        const result = requirement.every(ar => ar.func(value));
        console.log(result);
        currStatus(result);
    }, [value,requirement]);

    return (
        requirement.map((data,index) => (
            <Show
                key = {index}
                value = {value}
                data  = {data}
            />
        ))
    );
};

export default Validate;