const Requirement = [
    {
        title: "Must contains min 8 characters",
        func: val => val.length >= 8
    },
    {
        title: "Must contain at least one lower-case letter",
        func: val => /[a-z]/g.test(val),
    },
    {
        title: "Must contain at least one upper-case letter",
        func: val => /[A-Z]/g.test(val),
    },
];

export default Requirement;